CREATE TABLE `User` ( `userId` INT NOT NULL AUTO_INCREMENT, `userName` VARCHAR(255) NULL, `password` VARCHAR(255) NULL, `emailAddress` VARCHAR(255) NULL, `lastLogin` DATETIME NULL, `firstName` VARCHAR(255) NULL, `lastName` VARCHAR(255) NULL, PRIMARY KEY (`userId`), UNIQUE INDEX `userName_UNIQUE` (`userName` ASC), UNIQUE INDEX `userId_UNIQUE` (`userId` ASC), UNIQUE INDEX `emailAddress_UNIQUE` (`emailAddress` ASC));
